void Sweeper::ArmExtend(bool extend) {
	if(extend == true) {
		extension->Set(extension->kForward);
	}
	else {
		extension->Set(extension->kReverse);
	}
}


void Sweeper::SweeperSpeed(double speed) {
	motor->Set(speed);
}
