// Put methods for controlling this subsystem
// here. Call these from Commands.

void FloorConveyor::Initialize(void)
{
    spdlog::info("FC Init");
    // Motor off
    if (m_talonValidFC8)
    {
        m_motorFC8.Set(ControlMode::PercentOutput, 0.0);

        m_motorFC8.ConfigSelectedFeedbackSensor(FeedbackDevice::CTRE_MagEncoder_Relative, kCANTimeout);
        m_motorFC8.SetSensorPhase(false);
        m_motorFC8.SetSelectedSensorPosition(0, kCANTimeout);
    }
}

//    Dump all Talon faults
void FloorConveyor::FaultDump(void)
{
    frc2135::TalonUtils::TalonFaultDump("FC 8", m_motorFC8);
}

// Set mode of floor conveyor
void FloorConveyor::SetFloorConveyorSpeed(int direction)
{
    const char *strName;
    double outputFC = 0.0; // Default: off

    switch (direction)
    {
        default:
        case FCONVEYOR_STOP:
            strName = "STOP";
            outputFC = 0.0;
            break;
        case FCONVEYOR_ACQUIRE:
            strName = "ACQUIRE";
            outputFC = m_acquireSpeed;
            break;
        case FCONVEYOR_EXPEL:
            strName = "EXPEL";
            outputFC = m_expelSpeed;
            break;
    }

    spdlog::info("Floor Conveyor Set Speed - {}", strName);

    if (m_talonValidFC8)
        m_motorFC8.Set(ControlMode::PercentOutput, outputFC);
}
